from aiogram.fsm.state import StatesGroup, Stateclass AdminLoginState(StatesGroup):    password = State()class SendMessageToUser(StatesGroup):    text = State()    userId = State()class SendMessageToUsers(StatesGroup):    messageType = State()    buttons = State()    message = State()class AddAccountState(StatesGroup):    phoneNumber = State()    verificationCode = State()    password = State()class AccountSelectionState(StatesGroup):    accountName = State()class UserRegisterState(StatesGroup):    language = State()class AvailablePlayPassState(StatesGroup):    availablePlayPass = State()class PopupBalanceState(StatesGroup):    amount = State()class StarsAmount(StatesGroup):    amount = State()